// 预解析 var num; f1; f2;
var num= 123 ;
function f1 () {
   //2: 预解析没有声明提升
   console.log(num);//往函数外面找,为123
}
function f2 () {
   //预解析:var num;
   console.log(num); //undefined
   var num = 456; // 赋值:num=456;
   f1(); //1: 调用
   console.log(num); 
}
f2(); //0: 调用
//结果为: undefind 123 456

//扩展
(function(a){
   //预解析之前有参数的赋值 var a = 100;
   //预解析的时候0 var a ;//变量重复声明
   //预解析的时候1 a = function(){} ;//为变量添加一个函数的索引
   console.log(a);
   // 重复声明  等价于a=10;
   var a = 10;
