Function



// 1. 函数模式////////////////////////////////////
// this 表示全局对象
// 任何自调用都是函数调用模式
function foo () {
   console.log("1");
}
foo();
////////////////////////////////////////////////

// 2. 方法调用模式///////////////////////////////
// -> 区分
// 方法本生就是函数,但是是通过对象引导调用
// 对象.方法( 参数 )
// this 表示 引导调用的对象
function foo () {
   console.log(this);
}
var o = {name:"jack"];
o.foo = foo;

foo();// window
o.foo();// o对象

function func () {
   console.log("2"); 
}
func.foo = foo;
func.foo(); //打印函数体

var arr = [1,2,3];
arr.foo = foo; 
arr.foo(); //打印数组对象

var arr = [1,2,3,function(){
   console.log(this);
}] ;
arr[3]();// 打印数组
//////////////////////////////////////////////

// 3. 构造器模式(或者叫做构造函数模式,构造方法模式)/////
// 构造器的执行步骤
var p = new Person ();
